/**
 * Process data from the execution context in order to either reformat or create new data.
 * @param {Object} executionContext The execution context as a freezed object, you cannot update it directly.
 * @returns {Object} The result of your data processing which will be available in the next execution context.
 */
function main(executionContext) {
    // Declaration of the output variable.
    let output = {};

    // Récupération de la valeur du champ depuis le contexte d'exécution.
    let projectValue = executionContext?.actions?.choose_issue_type?.inputs?.issuetypes;
    //let projectValue = "10008 - placeholder issue type";

    // Vérification que la valeur existe et traitement.
    if (projectValue) {
        // Supposons que projectValue soit au format "id - name".
        let [id, name] = projectValue.split(" - ");

        // Assignation des valeurs à l'objet de sortie.
        output.id = id ? id.trim() : null;
        output.name = name ? name.trim() : null;
    } else {
        // Si la valeur n'existe pas, retourner un message d'erreur ou laisser vide.
        output.error = "The Project ID field is empty or cannot be found.";
    }

    // Mandatory: return your output.
    return output;
}

