/**
 * Process data from the execution context in order to either reformat or create new data.
 * @param {Object)  executionContext  The execution context as a freezed object, you cannot update it directly.
 * @returns {Object}                  The resultat of your data processing which will be available in the next execution context.
 */
function main(executionContext) {
    // Declaration of the output variable.
    let output = {};

    // Extraction des erreurs depuis le contexte d'exécution
    const errors = executionContext?.actions?.create_ticket?.response?.body?.errors || {};
	const errorMessages = executionContext?.actions?.create_ticket?.response?.body?.errorMessages || [];

    // Transformation des erreurs en un seul string
    let logMessage = "";

    // Ajout des messages d'erreurs simples
    if (errorMessages.length > 0) {
        logMessage += "Error: " + errorMessages.join(", ") + ". ";
    }

    // Ajout des erreurs clés/valeurs
    if (Object.keys(errors).length > 0) {
        logMessage += "Error Details : ";
        for (const [key, value] of Object.entries(errors)) {
            logMessage += `${key} - ${value}. `;
        }
    }

    // Ajout du message final au output
    output.log = logMessage.trim();

    // Mandatory: return your output.
    return output;
}

/**
 * Transform user input data into Jira-compatible format
 * @param {Object} executionContext The execution context containing the form inputs
 * @returns {Object} Jira-formatted ticket data
 */
function main(executionContext) {
    // Vérification de l'existence du contexte d'exécution
    if (!executionContext || typeof executionContext !== 'object') {
        return {
            json: {},
            log: 'Invalid or missing execution context'
        };
    }

    // Récupération des inputs du formulaire et du contexte
    const ticketInputs = executionContext?.actions?.ticket_form?.inputs;
    const fields = executionContext?.actions?.itmf?.response?.body?.projects?.[0]?.issuetypes?.[0]?.fields;

    if (!ticketInputs) {
        return {
            json: {},
            log: 'No ticket inputs found in execution context'
        };
    }

    if (!fields) {
        return {
            json: {},
            log: 'No fields metadata found in execution context'
        };
    }

    // Initialisation de la structure de base
    const output = {
        json: {
            fields: {},
            update: {}
        },
        log: ''
    };

    // Fonction helper pour créer un champ de type document
    const createDocField = (text) => ({
        content: [
            {
                content: [
                    {
                        text: text,
                        type: "text"
                    }
                ],
                type: "paragraph"
            }
        ],
        type: "doc",
        version: 1
    });

    // Fonction helper pour convertir la valeur selon le type
    const convertValue = (value, schema) => {
        if (value == null) return null;

        switch (schema?.type) {
            case 'number':
            case 'integer':
                return parseInt(value, 10);
            case 'array':
                if (!Array.isArray(value)) {
                    value = [value];
                }
                if (schema.items === 'component' || schema.items === 'version') {
                    return value.map(v => ({ id: v }));
                }
                return value;
            default:
                return value;
        }
    };

    // Ajout des champs projet et issuetype avec les IDs spécifiés
    output.json.fields["issuetype"] = { id: executionContext?.actions?.split_issue_name?.output?.id };
    output.json.fields["project"] = { id: executionContext?.actions?.split_project_name_id?.output?.id };

    // Mapping des champs d'entrée vers la structure Jira
    Object.entries(ticketInputs).forEach(([key, value]) => {
        // Si la valeur est null ou undefined, on skip
        if (value == null) return;

        // Récupération du schéma du champ depuis le contexte
        const fieldMetadata = fields[key];
        const schema = fieldMetadata?.schema;

        switch (key) {
            // Champs avec structure document
            case 'description':
            case 'environment':
                output.json.fields[key] = createDocField(value);
                break;

            // Champs avec ID simple
            case 'priority':
            case 'security':
            case 'assignee':
            case 'reporter':
                output.json.fields[key] = { id: value };
                break;

            // Champs avec ID dans un tableau
            case 'components':
            case 'fixVersions':
            case 'versions':
                output.json.fields[key] = value ? [{ id: value }] : [];
                break;

            // Champs personnalisés
            default:
                if (key.startsWith('customfield_')) {
                    if (schema?.type === 'option') {
                        output.json.fields[key] = { id: value };
                    } else if (schema?.type === 'array' && schema.items === 'option') {
                        output.json.fields[key] = Array.isArray(value) ? value.map(v => ({ id: v })) : [{ id: value }];
                    } else {
                        output.json.fields[key] = convertValue(value, schema);
                    }
                } else {
                    output.json.fields[key] = convertValue(value, schema);
                }
        }
    });

    // Ajout du log formaté
    output.log = JSON.stringify(output.json, null, 2);

    return output;
}

function main(executionContext) {
    let output = {};

    // Fonction pour normaliser les noms (remplacer espaces, retirer accents, convertir en minuscule)
    const normalizeName = (name) => {
        return name
            .toLowerCase()
            .replace(/\s+/g, '_') // Remplace les espaces par des underscores
            .normalize("NFD") // Décompose les caractères accentués en caractères simples
            .replace(/[\u0300-\u036f]/g, ''); // Supprime les accents
    };

    // Récupérer l'ID du type de ticket sélectionné
    const selectedIssueTypeId = executionContext?.actions?.split_issue_name?.output?.id;

    // Accéder à la liste des projets et vérifier leur présence
    const projects = executionContext?.actions?.itmf?.response?.body?.projects || [];

    // Parcourir chaque projet
    projects.forEach(project => {
        const issueTypes = project?.issuetypes || [];

        // Parcourir chaque type de ticket
        issueTypes.forEach(issueType => {
            if (issueType.id === selectedIssueTypeId) {
                const fields = issueType?.fields || {};

                // Normaliser le nom du type de ticket
                const issueTypeName = normalizeName(issueType.name);

                // Extraire les champs principaux (résumé, description, priorité)
                const primaryFields = [
                    "summary",
                    "description",
                    "priority"
                ].map(key => ({
                    key,
                    name: fields[key]?.name || "Name not specified",
                    required: fields[key]?.required || false,
                    description: fields[key]?.description || "Description not specified",
                    type: fields[key]?.schema?.type || "Type not specified",
                    system: fields[key]?.schema?.system || false,
                    custom: fields[key]?.schema?.custom || false,
                    allowedValues: fields[key]?.allowedValues || []
                }));

                // Extraire les autres champs
                const otherFields = Object.keys(fields)
                    .filter(fieldKey => !["summary", "description", "priority"].includes(fieldKey))
                    .map(fieldKey => ({
                        key: fieldKey,
                        name: fields[fieldKey]?.name || "Name not specified",
                        required: fields[fieldKey]?.required || false,
                        description: fields[fieldKey]?.description || "Description not specified",
                        type: fields[fieldKey]?.schema?.type || "Type not specified",
                        system: fields[fieldKey]?.schema?.system || false,
                        custom: fields[fieldKey]?.schema?.custom || false,
                        allowedValues: fields[fieldKey]?.allowedValues || []
                    }));

                // Ajouter tous les champs dans l'ordre désiré
                output.fields = [...primaryFields, ...otherFields];
                output.selectedIssueTypeId = selectedIssueTypeId;
            }
        });
    });

    return output;
}

