/**
 * Process data from the execution context in order to either reformat or create new data.
 * @param {Object} executionContext The execution context as a freezed object, you cannot update it directly.
 * @returns {Object} The result of your data processing which will be available in the next execution context.
 */
function main(executionContext) {
    let output;

    const { startDate, startDatePeriod, endDate, endDatePeriod, typeofleave } = executionContext.actions.ac.inputs;
/*
    const startDate = "2024-12-04";
    const endDate = "2024-12-04";
    const endDatePeriod = "T18:00:00.000Z";
    const startDatePeriod = "T08:00:00.000Z";
    const typeofleave = "e7363fe834bd4c2883d36652ac6c979a";
*/
    // Parse the dates
    const startDateTime = new Date(`${startDate}${startDatePeriod}`);
    const endDateTime = new Date(`${endDate}${endDatePeriod}`);

    // Check if the start and end dates are on different days
    const msPerDay = 1000 * 60 * 60 * 24;
    const isSameDay = startDateTime.toDateString() === endDateTime.toDateString();

    let totalRequestedHours = 0;
    let days = []; // To store the detailed information for each day

    if (!isSameDay) {
        // If the start and end date are different
        const fullDays = Math.max(0, Math.floor((endDateTime - startDateTime) / msPerDay) - 1);
        const hoursFullDays = fullDays * 8; // Each full workday is 8 hours (4 in the morning + 4 in the afternoon)

        // Calculate hoursFirstDay
        let hoursFirstDay = 0;
        if (startDatePeriod === "T08:00:00.000Z") {
            hoursFirstDay = 8;
            days.push({
                start: `${startDate}T08:00:00.000Z`,
                date: `${startDate}`,
                end: `${startDate}T18:00:00.000Z`,
                dailyQuantity: 8,
                timeOffType: { descriptor: typeofleave, id: typeofleave }
            });
        } else if (startDatePeriod === "T14:00:00.000Z") {
            hoursFirstDay = 4;
            days.push({
                start: `${startDate}T14:00:00.000Z`,
                date: `${startDate}`,
                end: `${startDate}T18:00:00.000Z`,
                dailyQuantity: 4,
                timeOffType: { descriptor: typeofleave, id: typeofleave }
            });
        }

        // Calculate hoursLastDay
        let hoursLastDay = 0;
        if (endDatePeriod === "T12:00:00.000Z") {
            hoursLastDay = 4;
            days.push({
                start: `${endDate}T08:00:00.000Z`,
                date: `${endDate}`,
                end: `${endDate}T12:00:00.000Z`,
                dailyQuantity: 4,
                timeOffType: { descriptor: typeofleave, id: typeofleave }
            });
        } else if (endDatePeriod === "T18:00:00.000Z") {
            hoursLastDay = 8;
            days.push({
                start: `${endDate}T08:00:00.000Z`,
                date: `${endDate}`,
                end: `${endDate}T18:00:00.000Z`,
                dailyQuantity: 8,
                timeOffType: { descriptor: typeofleave, id: typeofleave }
            });
        }

        // Add the full days in between
        for (let i = 1; i <= fullDays; i++) {
            const middleDay = new Date(startDateTime);
            middleDay.setDate(startDateTime.getDate() + i);
            const middleDate = middleDay.toISOString().split("T")[0];

            days.push({
                start: `${middleDate}T08:00:00.000Z`,
                date: middleDate,
                end: `${middleDate}T18:00:00.000Z`,
                dailyQuantity: 8,
                timeOffType: { descriptor: typeofleave, id: typeofleave }
            });
        }

        totalRequestedHours = hoursLastDay + hoursFirstDay + hoursFullDays;
    } else {
        // If the start and end date are the same
        if (startDatePeriod === "T08:00:00.000Z" && endDatePeriod === "T18:00:00.000Z") {
            totalRequestedHours = 8;
            days.push({
                start: `${startDate}T08:00:00.000Z`,
                date: `${startDate}`,
                end: `${startDate}T18:00:00.000Z`,
                dailyQuantity: 8,
                timeOffType: { descriptor: typeofleave, id: typeofleave }
            });
        } else if (startDatePeriod === "T14:00:00.000Z" && endDatePeriod === "T18:00:00.000Z") {
            totalRequestedHours = 4;
            days.push({
                start: `${startDate}T14:00:00.000Z`,
                date: `${startDate}`,
                end: `${startDate}T18:00:00.000Z`,
                dailyQuantity: 4,
                timeOffType: { descriptor: typeofleave, id: typeofleave }
            });
        } else if (startDatePeriod === "T08:00:00.000Z" && endDatePeriod === "T12:00:00.000Z") {
            totalRequestedHours = 4;
            days.push({
                start: `${startDate}T08:00:00.000Z`,
                date: `${startDate}`,
                end: `${startDate}T12:00:00.000Z`,
                dailyQuantity: 4,
                timeOffType: { descriptor: typeofleave, id: typeofleave }
            });
        } else {
            // Error case: start period is after end period
            output = {
                success: "false",
                message: "Error: start date period cannot be after end date period."
            };
            return output;
        }
    }

    // Access the timeoffdetails array from the mapping object
    // Accéder aux détails des congés
    const timeoffdetails = executionContext.actions.mapping.output.timeoffdetails;

    // Rechercher le plan correspondant à l'ID du type de congé
    const absencePlan = timeoffdetails.find(detail => detail.timeofftypeid === typeofleave);

    if (absencePlan) {
        // Convertir la quantité en un nombre (float)
        const availableHours = parseFloat(absencePlan.quantity);

        // Vérifier si les heures demandées sont disponibles
        if (totalRequestedHours <= availableHours) {
			output = days;
        } else {
            output = {
                success: "false",
                message: `Insufficient balance in '${absencePlan.timeofftypedescriptor}'. Requested: ${totalRequestedHours} hours, Available: ${availableHours} hours.`,
                days
            };
        }
    } else {
        output = {
            success: "false",
            message: `Absence plan with ID '${typeofleave}' not found.`,
            days
        };
    }

    return output;

}

/**
 * Process data from the execution context in order to either reformat or create new data.
 * @param {Object} executionContext The execution context as a freezed object, you cannot update it directly.
 * @returns {Object} The result of your data processing which will be available in the next execution context.
 */
function main(executionContext) {
    // Declaration of the output variable.
    let output = {};

    // Récupération des tableaux nécessaires.
    const eligibleTimes = executionContext.actions.geteligibletimes.response.body.data || [];
    const balances = executionContext.actions.getbalances.response.body.data || [];

    // Préparation d'une structure facilitant le mapping.
    // On extrait les noms des timeoffs disponibles à partir de balances.
    const availableTimeOffs = {};
    balances.forEach(item => {
        // Extraire les types d'absences regroupées dans `timeoffs` et les nettoyer.
        const timeoffTypes = item.absencePlan.timeoffs
            .replace(/^\(/, '') // Supprime uniquement la première parenthèse ouvrante.
            .replace(/\)$/, '') // Supprime uniquement la dernière parenthèse fermante.
            .split(',') // Sépare les différents descripteurs.
            .map(descriptor => descriptor.trim()); // Supprime les espaces superflus.

        // Ajouter chaque type d'absence au dictionnaire pour un mapping rapide.
        timeoffTypes.forEach(type => {
            availableTimeOffs[type] = {
                quantity: item.quantity,
                unit: item.unit.descriptor
            };
        });
    });


    // Construction du tableau de sortie.
    const result = eligibleTimes
        .filter(timeoff => availableTimeOffs[timeoff.descriptor]) // Filtrer sur les types existants.
        .map(timeoff => ({
            timeofftypedescriptor: timeoff.descriptor,
            quantity: availableTimeOffs[timeoff.descriptor].quantity,
            unit: availableTimeOffs[timeoff.descriptor].unit,
            timeofftypeid: timeoff.id
        }));

    // Assigner le résultat à la sortie.
    output.timeoffdetails = result;

    // Mandatory: return your output.
    return output;
}
